(()=>{"use strict";const t=new class{constructor(t){this.initialTime=Number(t),this.time=this.initialTime,this.element=document.querySelector(".timer"),this.element.innerHTML=this.formatTime(this.time)}start(){this.intervalId=setInterval((()=>{this.time<=0?clearInterval(this.intervalId):(this.time--,this.element.innerHTML=this.formatTime(this.time))}),1e3)}stop(){clearInterval(this.intervalId)}reset(){clearInterval(this.intervalId),this.time=this.initialTime,this.element.innerHTML=this.formatTime(this.time)}formatTime(t){return`${Math.floor(t/60).toString().padStart(2,"0")}:${(t%60).toString().padStart(2,"0")}`}}(100);document.querySelector(".start").addEventListener("click",(()=>t.start())),document.querySelector(".stop").addEventListener("click",(()=>t.stop())),document.querySelector(".reset").addEventListener("click",(()=>t.reset()))})();